/* Procedure structure for procedure `addPC` */

/*!50003 DROP PROCEDURE IF EXISTS  `addPC` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `addPC`(
 IN pserial VARCHAR(17),
 IN plab VARCHAR(5),
 IN pPnum VARCHAR(10),
 IN pHDD VARCHAR(100),
 IN pRam VARCHAR(100),
 IN pProcesor VARCHAR(100),
 IN pSysType VARCHAR(100),
 IN pStatus VARCHAR(20))
BEGIN
 INSERT INTO tblpc(pcSerialNr,labNr,current_pc_nr,pcHDD, pcProcersor, pcRam, pcSystemType,pcStatus)
 VALUES(pserial,plab,pPnum,pHDD, pRam, pProcesor, pSysType,pStatus);
 UPDATE tbllab set labNrOfPCs=labNrOfPCs+1 WHERE labNr=plab;
 END */$$
DELIMITER ;

